package de.lotto_rlp.aufgabeZwei;

public class QuadratDrei {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i <= 4; i++) {
			
			for(int j = 4 ; j >= i ; j--) {
					
					System.out.print("X");
					
				}
				System.out.println();	
			}
			
		}		
		
	}